import React, { Component } from 'react';

class ContactForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      email: '',
      phoneNumber: '',
      errors: {
        firstName: '',
        lastName: '',
        email: '',
        phoneNumber: ''
      },
      successMessage: ''
    };
  }

  handleInputChange(event) {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  }

  handleSubmit(event) {
    event.preventDefault();

    if (this.validateForm()) {
      // Form submission logic goes here (e.g., API call, etc.)
      console.log('Form submitted successfully!');
      this.setState({
        successMessage: 'Form submitted successfully!',
        firstName: '',
        lastName: '',
        email: '',
        phoneNumber: '',
        errors: {
          firstName: '',
          lastName: '',
          email: '',
          phoneNumber: ''
        }
      });
    } else {
      console.log('Form has errors. Please fix them.');
      this.setState({ successMessage: '' });
    }
  }

  validateForm() {
    const { firstName, lastName, email, phoneNumber } = this.state;
    const errors = {
      firstName: '',
      lastName: '',
      email: '',
      phoneNumber: ''
    };
    let isValid = true;

    if (!firstName.trim()) {
      errors.firstName = 'First name is required';
      isValid = false;
    }

    if (!lastName.trim()) {
      errors.lastName = 'Last name is required';
      isValid = false;
    }

    if (!email.trim()) {
      errors.email = 'Email is required';
      isValid = false;
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      errors.email = 'Invalid email format';
      isValid = false;
    }

    if (!phoneNumber.trim()) {
      errors.phoneNumber = 'Phone number is required';
      isValid = false;
    } else if (!/^\d{10}$/.test(phoneNumber)) {
      errors.phoneNumber = 'Invalid phone number format';
      isValid = false;
    }

    this.setState({ errors });
    return isValid;
  }

  render() {
    const { firstName, lastName, email, phoneNumber, errors, successMessage } = this.state;

    return (
      <form onSubmit={this.handleSubmit.bind(this)}>
        <div>
          <label>First Name:</label>
          <input type="text" name="firstName" value={firstName} onChange={this.handleInputChange.bind(this)} />
          {errors.firstName && <span>{errors.firstName}</span>}
        </div>

        <div>
          <label>Last Name:</label>
          <input type="text" name="lastName" value={lastName} onChange={this.handleInputChange.bind(this)} />
          {errors.lastName && <span>{errors.lastName}</span>}
        </div>

        <div>
          <label>Email:</label>
          <input type="email" name="email" value={email} onChange={this.handleInputChange.bind(this)} />
          {errors.email && <span>{errors.email}</span>}
        </div>

        <div>
          <label>Phone Number:</label>
          <input type="text" name="phoneNumber" value={phoneNumber} onChange={this.handleInputChange.bind(this)} />
          {errors.phoneNumber && <span>{errors.phoneNumber}</span>}
        </div>

        {successMessage && <div>{successMessage}</div>}

        <button type="submit">Submit</button>
      </form>
    );
  }
}

export default ContactForm;
